package ejerciciosFlujos;

public class Ejercicio01App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 10;
		if(a > b) {
			System.out.println("El valor a: " + a + " es mayor que el valor b: "+ b);
		}else if(a < b) {
			System.out.println("El valor b: " + b + " es mayor que el valor a: "+ a);
		}else {
			System.out.println("El valor a: " + a + " es igual al valor b: "+ b);
		}
	}

}
